mapper.select:
SELECT A.SEQ, A.COURSE_ID, TITLE, A.USER_ID, B.USER_NAME, VIEW_CNT, DATE_FORMAT(IFNULL(A.UPDATE_DATE,A.CREATE_DATE), '%y.%m.%d %H:%i') CREATE_DATE, IF(A.USER_ID = #{USER_ID}, 'Y','N') MY_BOARD_YN FROM BOARD_REPORT A LEFT OUTER JOIN USER B ON A.USER_ID = B.USER_ID WHERE A.COURSE_ID = #{COURSE_ID}
AND ( UPPER(A.TITLE) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%') OR UPPER(A.USER_ID) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%') OR UPPER(B.USER_NAME) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%') )
ORDER BY 7 DESC

mapper.update:
UPDATE BOARD_REPORT
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE SEQ = #{SEQ}

mapper.select:
SELECT A.SEQ, A.COURSE_ID, TITLE, A.USER_ID, B.USER_NAME, VIEW_CNT, DATE_FORMAT(IFNULL(A.UPDATE_DATE,A.CREATE_DATE), '%y.%m.%d %H:%i') CREATE_DATE, IF(A.USER_ID = #{USER_ID}, 'Y','N') MY_BOARD_YN FROM BOARD_DATA A LEFT OUTER JOIN USER B ON A.USER_ID = B.USER_ID WHERE A.COURSE_ID = #{COURSE_ID}
AND ( UPPER(A.TITLE) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%') OR UPPER(A.USER_ID) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%') OR UPPER(B.USER_NAME) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%') )
ORDER BY 7 DESC

mapper.update:
UPDATE BOARD_DATA
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE SEQ = #{SEQ}

UPDATE BOARD_NOTICE
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE SEQ = #{SEQ}

mapper.select:
SELECT A.SEQ, SUBSTR(B.DD_MAIN,5,3) COMP_KIND, CATEGORY, TITLE, VIEW_CNT, DATE_FORMAT(IFNULL(A.UPDATE_DATE,A.CREATE_DATE), '%y.%m.%d %H:%i') CREATE_DATE FROM BOARD_FAQ A LEFT OUTER JOIN ( SELECT * FROM CODE WHERE DD_MAIN IN ('FAQ_B2C','FAQ_B2B','FAQ_C2C') ) B ON B.DD_KEY = A.CATEGORY WHERE 1 = 1
AND SUBSTR(B.DD_MAIN,5,3) = #{CB_COMPANY}
AND CATEGORY = #{CATEGORY}
AND UPPER(A.TITLE) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%')
ORDER BY 5 DESC

mapper.update:
UPDATE BOARD_FAQ
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE SEQ = #{SEQ}

