mapper.select:
SELECT A.SEQ, A.COURSE_ID, A.REF, A.ORD, A.STEP, TITLE, A.USER_ID, B.USER_NAME, VIEW_CNT, DATE_FORMAT(IFNULL(A.UPDATE_DATE,A.CREATE_DATE), '%y.%m.%d %H:%i') CREATE_DATE, IF(A.USER_ID = #{USER_ID}, 'Y','N') MY_BOARD_YN FROM BOARD_DISCUSSION A LEFT OUTER JOIN USER B ON A.USER_ID = B.USER_ID WHERE A.COURSE_ID = #{COURSE_ID}
AND ( UPPER(A.TITLE) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%') OR UPPER(A.USER_ID) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%') OR UPPER(B.USER_NAME) LIKE CONCAT('%',UPPER(#{SEARCH_STR}),'%') )
ORDER BY REF DESC,ORD

mapper.update:
UPDATE BOARD_DISCUSSION
   SET REF = #{REF}
 WHERE SEQ = #{SEQ}

UPDATE BOARD_DISCUSSION
   SET ORD = ORD + 1
 WHERE COURSE_ID = #{COURSE_ID}
   AND REF = #{REF}
   AND ORD > #{ORD}

UPDATE BOARD_DISCUSSION
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE SEQ = #{seq}
