mapper.update:
		UPDATE 	COMPANY
    	   SET 	COMP_NAME = #{COMP_NAME}
				,SUB_DOMAIN = #{SUB_DOMAIN}
				,ADDR = #{ADDR}
				,ZIPCODE = #{ZIPCODE}
				,COMP_TEL = #{COMP_TEL}
				,FAX = #{FAX}
				,BUSINESS_NO = #{BUSINESS_NO}
				,OWNER_NAME = #{OWNER_NAME}
				,LOGIN_IMG = #{LOGIN_IMG}
				,GNB_IMG = #{GNB_IMG}
				,MOBILE = #{MOBILE}
				,C2C_USER_ID = #{C2C_USER_ID}
				,USE_YN = #{USE_YN}
    	       	,UPDATE_DATE = CURRENT_TIMESTAMP
    	       	,UPDATE_USER = #{SESSION_USER_ID}
    	 WHERE 	COMP_CD = #{COMP_CD}

mapper.select:
		SELECT 	COUNT(*) CNT
    	FROM 	COMPANY
    	WHERE 	COMP_CD = #{COMP_CD}

	SELECT 	A.*
	FROM 	COMPANY A                 
 	WHERE 	COMP_CD = #{COMP_CD}

mapper.update:
		UPDATE 	COMPANY
    	   SET 	UPDATE_DATE = CURRENT_TIMESTAMP
    	       	,UPDATE_USER = #{SESSION_USER_ID}
		    ,LOGIN_IMG = 'Y'
		    ,GNB_IMG = 'Y'
    	 WHERE 	COMP_CD = #{COMP_CD}
