mapper.select:
SELECT A.* ,DATE_FORMAT(CREATE_DATE, '%y.%m.%d %H:%i') CREATE_DATE_STR ,DATE_FORMAT(UPDATE_DATE, '%y.%m.%d %H:%i') UPDATE_DATE_STR FROM USER_INQUIRY A WHERE DATE_FORMAT(CREATE_DATE, '%Y-%m-%d') BETWEEN #{FROM_DT} AND #{TO_DT} AND KIND = #{CB_KIND}
AND ( USER_NAME LIKE CONCAT('%',#{SEARCH_STR},'%') OR MOBILE LIKE CONCAT('%',#{SEARCH_STR},'%') OR TEL LIKE CONCAT('%',#{SEARCH_STR},'%') OR EMAIL LIKE CONCAT('%',#{SEARCH_STR},'%') )
ORDER BY CREATE_DATE DESC
