mapper.select:
SELECT B.CATEGORY_NAME, A.COURSE_NAME, A.COURSE_CODE, ifnull(E.QUESTION_ALL_CNT, 0) QUESTION_ALL_CNT, ifnull(D.QUESTION_TYPE_CNT, 0) QUESTION_TYPE_CNT, C.EXAM_RATE, (SELECT COUNT(*) FROM COURSE_RESOURCE WHERE COURSE_CODE = A.COURSE_CODE) WEEK_CNT FROM COURSE_CODE A LEFT OUTER JOIN ( SELECT C3.CODE C3_CODE, C2.CODE C2_CODE, C1.CODE C1_CODE, CONCAT(C1.CODE_NAME,' > ',C2.CODE_NAME,' > ',C3.CODE_NAME) CATEGORY_NAME FROM CATEGORY C3 LEFT OUTER JOIN CATEGORY C2 ON C3.PARENT_CODE = C2.CODE LEFT OUTER JOIN CATEGORY C1 ON C2.PARENT_CODE = C1.CODE WHERE C3.DEPTH = 3) B ON A.CODE = B.C3_CODE JOIN COURSE_MASTER C ON C.COURSE_CODE = A.COURSE_CODE LEFT OUTER JOIN ( SELECT COURSE_CODE, COUNT(*) QUESTION_TYPE_CNT FROM COURSE_EXAM_TYPE WHERE USE_YN = 'Y' GROUP BY COURSE_CODE ) D ON D.COURSE_CODE = A.COURSE_CODE LEFT OUTER JOIN ( SELECT COURSE_CODE, COUNT(*) QUESTION_ALL_CNT FROM COURSE_EXAM WHERE USE_YN = 'Y' GROUP BY COURSE_CODE ) E ON E.COURSE_CODE = A.COURSE_CODE WHERE C.EXAM_RATE > 0 AND A.USE_YN = 'Y'
AND B.C1_CODE = #{LEVEL1_CODE}
AND B.C2_CODE = #{LEVEL2_CODE}
AND B.C3_CODE = #{LEVEL3_CODE}
AND A.COURSE_NAME LIKE CONCAT('%',#{courseName},'%')
AND A.CONTENTS_MANAGER_ID = #{SESSION_USER_ID}
ORDER BY B.CATEGORY_NAME,A.COURSE_NAME

mapper.update:
UPDATE 	COURSE_EXAM
   SET 	TYPE = #{TYPE}
		,QUESTION = #{QUESTION}
		,QA1 = #{QA1}
		,QA2 = #{QA2}
		,QA3 = #{QA3}
		,QA4 = #{QA4}
		,ANSWER = #{ANSWER}
		,ANSWER_DESC = #{ANSWER_DESC}
		,WEEK = IFNULL(#{WEEK}, 1)
		,LEVEL = IFNULL(#{LEVEL}, 3)
		,USE_YN = #{USE_YN}
		,ORD = #{ORD}
       	,UPDATE_DATE = CURRENT_TIMESTAMP
       	,UPDATE_USER = #{SESSION_USER_ID}
 WHERE 	COURSE_CODE = #{COURSE_CODE}
   AND 	SEQ = #{SEQ}

mapper.delete:
DELETE 	FROM COURSE_EXAM
 WHERE 	COURSE_CODE = #{COURSE_CODE}
   AND 	SEQ = #{SEQ}
