mapper.select:
SELECT A.SEQ, C.CATEGORY_NAME, A.COURSE_CODE, B.COURSE_NAME, A.TYPE_NAME, A.QUESTION_CNT, A.USE_YN, IFNULL(D.STANDARD_CNT1, 0) STANDARD_CNT1, IFNULL(D.STANDARD_QUESTION_CNT_G1, 0) STANDARD_QUESTION_CNT_G1, IFNULL(D.STANDARD_QUESTION_CNT_J1, 0) STANDARD_QUESTION_CNT_J1, IFNULL(D.STANDARD_CNT2, 0) STANDARD_CNT2, IFNULL(D.STANDARD_QUESTION_CNT_G2, 0) STANDARD_QUESTION_CNT_G2, IFNULL(D.STANDARD_QUESTION_CNT_J2, 0) STANDARD_QUESTION_CNT_J2, IFNULL(E.QUESTION_ALL_CNT, 0) QUESTION_ALL_CNT, IFNULL(F.WEEK_CNT, 0) WEEK_CNT, IFNULL(A.TOTAL_RATIO, 0) TOTAL_RATIO, IFNULL(A.WEEK_RATIO, 0) WEEK_RATIO, DATE_FORMAT(IFNULL(A.UPDATE_DATE, A.CREATE_DATE), '%y.%m.%d %H:%i') LAST_UPDATE_DATE, IFNULL(U2.USER_NAME, U1.USER_NAME) LAST_UPDATE_USER, 'N' NEW_FLAG FROM COURSE_EXAM_TYPE A LEFT JOIN COURSE_CODE B ON A.COURSE_CODE = B.COURSE_CODE AND B.USE_YN = 'Y' LEFT OUTER JOIN ( SELECT C3.CODE C3_CODE, C2.CODE C2_CODE, C1.CODE C1_CODE, CONCAT(C1.CODE_NAME,' > ',C2.CODE_NAME,' > ',C3.CODE_NAME) CATEGORY_NAME FROM CATEGORY C3 LEFT OUTER JOIN CATEGORY C2 ON C3.PARENT_CODE = C2.CODE LEFT OUTER JOIN CATEGORY C1 ON C2.PARENT_CODE = C1.CODE WHERE C3.DEPTH = 3) C ON B.CODE = C.C3_CODE LEFT OUTER JOIN ( SELECT EXAM_TYPE_SEQ, SUM(IF(EXAM_KIND = 'TOTAL', 1, 0)) STANDARD_CNT1, SUM(IF(EXAM_KIND = 'TOTAL' AND TYPE = 'G', QUESTION_CNT, 0)) STANDARD_QUESTION_CNT_G1, SUM(IF(EXAM_KIND = 'TOTAL' AND TYPE = 'J', QUESTION_CNT, 0)) STANDARD_QUESTION_CNT_J1, SUM(IF(EXAM_KIND = 'WEEK', 1, 0)) STANDARD_CNT2, SUM(IF(EXAM_KIND = 'WEEK' AND TYPE = 'G', QUESTION_CNT, 0)) STANDARD_QUESTION_CNT_G2, SUM(IF(EXAM_KIND = 'WEEK' AND TYPE = 'J', QUESTION_CNT, 0)) STANDARD_QUESTION_CNT_J2 FROM COURSE_EXAM_TYPE_STANDARD WHERE USE_YN = 'Y' GROUP BY EXAM_TYPE_SEQ ) D ON D.EXAM_TYPE_SEQ = A.SEQ LEFT OUTER JOIN ( SELECT COURSE_CODE, COUNT(*) QUESTION_ALL_CNT FROM COURSE_EXAM WHERE USE_YN = 'Y' GROUP BY COURSE_CODE ) E ON E.COURSE_CODE = A.COURSE_CODE LEFT OUTER JOIN ( SELECT COURSE_CODE, COUNT(*) WEEK_CNT FROM COURSE_RESOURCE GROUP BY COURSE_CODE) F ON F.COURSE_CODE = A.COURSE_CODE LEFT OUTER JOIN USER U1 ON A.CREATE_USER = U1.USER_ID LEFT OUTER JOIN USER U2 ON A.UPDATE_USER = U2.USER_ID WHERE 1 = 1
AND C.C1_CODE = #{LEVEL1_CODE}
AND C.C2_CODE = #{LEVEL2_CODE}
AND C.C3_CODE = #{LEVEL3_CODE}
AND B.COURSE_NAME LIKE CONCAT('%',#{courseName},'%')
ORDER BY C.CATEGORY_NAME,B.COURSE_NAME

mapper.update:
UPDATE 	COURSE_EXAM_TYPE
   SET 	TYPE_NAME = #{TYPE_NAME}
		,QUESTION_CNT = #{QUESTION_CNT}
		,USE_YN = #{USE_YN}
		,TOTAL_RATIO = #{TOTAL_RATIO}
		,WEEK_RATIO = #{WEEK_RATIO}
       	,UPDATE_DATE = CURRENT_TIMESTAMP
       	,UPDATE_USER = #{SESSION_USER_ID}
 WHERE 	SEQ = #{SEQ}

UPDATE 	COURSE_EXAM_TYPE_STANDARD
   SET 	EXAM_KIND = #{EXAM_KIND}
		,WEEK_FROM = #{WEEK_FROM}
		,WEEK_TO = #{WEEK_TO}
		,TYPE = #{TYPE}
		,LEVEL = #{LEVEL}
		,QUESTION_CNT = #{QUESTION_CNT}
		,USE_YN = #{USE_YN}
       	,UPDATE_DATE = CURRENT_TIMESTAMP
       	,UPDATE_USER = #{SESSION_USER_ID}
 WHERE 	SEQ = #{SEQ}
