mapper.update:
UPDATE QUEST_GROUP
   SET GROUP_NAME = #{GROUP_NAME},
       USE_YN = #{USE_YN},
       UPDATE_DATE = CURRENT_TIMESTAMP,
       UPDATE_USER = #{SESSION_USER_ID}
 WHERE QG_ID = #{QG_ID}

UPDATE QUEST
   SET TYPE = #{TYPE}
       ,QUESTION = #{QUESTION}
       ,QA1 = #{QA1}
       ,QA2 = #{QA2}
       ,QA3 = #{QA3}
       ,QA4 = #{QA4}
       ,USE_YN = #{USE_YN}
       ,ORD = #{ORD}
       ,UPDATE_DATE = CURRENT_TIMESTAMP
       ,UPDATE_USER = #{SESSION_USER_ID}
 WHERE QG_ID = #{QG_ID}   
   AND SEQ = #{SEQ}

mapper.delete:
DELETE FROM QUEST
 WHERE QG_ID = #{QG_ID}   
   AND SEQ = #{SEQ}
