mapper.update:
		UPDATE 	BANK
    	   SET 	BANK_NAME = #{BANK_NAME}
				,ACC_NO = #{ACC_NO}
				,USER = #{USER}
				,USE_YN = #{USE_YN}
    	 WHERE 	SEQ = #{SEQ}

		UPDATE 	MAIL
    	   SET 	KIND = #{KIND}
				,EMAIL = #{EMAIL}
    	 WHERE 	SEQ = #{SEQ}

		UPDATE 	SETTING
    	   SET 	OPTION_VALUE = #{OPTION_VALUE}
				,OPTION_DESC = #{OPTION_DESC}
    	 WHERE 	SEQ = #{SEQ}

		UPDATE 	CODE
    	SET 	DD_VALUE = #{DD_VALUE}
				,ORD = #{ORD}
    	WHERE 	DD_MAIN = #{DD_MAIN} 
    	AND		DD_KEY = #{DD_KEY}
