mapper.update:
		UPDATE 	BANK
    	   SET 	BANK_NAME = #{BANK_NAME}
				,ACC_NO = #{ACC_NO}
				,USER = #{USER}
				,USE_YN = #{USE_YN}
    	 WHERE 	SEQ = #{SEQ}

		UPDATE 	MAIL
    	   SET 	KIND = #{KIND}
				,EMAIL = #{EMAIL}
    	 WHERE 	SEQ = #{SEQ}

		UPDATE 	SETTING
    	   SET 	OPTION_VALUE = #{OPTION_VALUE}
				,OPTION_DESC = #{OPTION_DESC}
    	 WHERE 	SEQ = #{SEQ}

		UPDATE 	CODE
    	SET 	DD_VALUE = #{DD_VALUE}
				,ORD = #{ORD}
    	WHERE 	DD_MAIN = #{DD_MAIN} 
    	AND		DD_KEY = #{DD_KEY}

mapper.select:
SELECT A.* ,'N' NEW_FLAG FROM MAIN_FRAME A WHERE 1 = 1
AND CHANNEL_KIND = #{CHANNEL_KIND}
ORDER BY T_ORD

mapper.update:
		UPDATE 	MAIN_FRAME
    	SET 	CHANNEL_KIND = #{CHANNEL_KIND}
				,FRAME_KIND = #{FRAME_KIND}
				,FRAME_NAME = #{FRAME_NAME}
				,FRAME_DESC = #{FRAME_DESC}
				,ORD = #{ORD}
				,USE_YN = #{USE_YN}
				,T_FRAME_NAME = #{T_FRAME_NAME}
				,T_FRAME_DESC = #{T_FRAME_DESC}
				,T_ORD = #{T_ORD}
				,T_USE_YN = #{T_USE_YN}
    	 WHERE 	SEQ = #{SEQ}

		UPDATE 	MAIN_FRAME_DETAIL
    	SET 	BK_COLOR = #{BK_COLOR}
				,BK_IMAGE_URL = #{BK_IMAGE_URL}
				,LINK_URL = #{LINK_URL}
				,MP4_YN = #{MP4_YN}
				,ORD = #{ORD}
				,USE_YN = #{USE_YN}
				,T_BK_COLOR = #{T_BK_COLOR}
				,T_BK_IMAGE_URL = #{T_BK_IMAGE_URL}
				,T_LINK_URL = #{T_LINK_URL}
				,T_MP4_YN = #{T_MP4_YN}
				,T_ORD = #{T_ORD}
				,T_USE_YN = #{T_USE_YN}
    	 WHERE 	SEQ = #{SEQ}
