mapper.select:
SELECT COUNT(*) CNT
  FROM USER
 WHERE USER_ID = #{userId}

SELECT COUNT(*) CNT
  FROM USER
 WHERE EMAIL = #{email}

SELECT COUNT(*) CNT
  FROM USER
 WHERE USER_ID = #{recommendId}

SELECT COUNT(*) CNT
  FROM COURSE_REGISTER
 WHERE USER_ID = #{userId}

SELECT USER_ID
  FROM USER
 WHERE CERTIFICATION_KEY = #{certificationKey}

UPDATE USER
   SET CERTIFICATION_YN = 'Y'
 WHERE USER_ID = #{userId}

SELECT COUNT(*) CNT
  FROM USER
 WHERE USER_NAME = #{userName}
   AND BIRTH_DAY = #{birthDay}
