mapper.select:
SELECT DATA.* FROM ( SELECT A.USER_ID ,B.USER_NAME ,B.EMAIL ,B.MOBILE ,IFNULL(C.EVAL,0) EVAL ,C.CONTENTS FROM COURSE_REGISTER A LEFT OUTER JOIN USER B ON A.USER_ID = B.USER_ID LEFT OUTER JOIN POSTSCRIPT C ON C.COURSE_ID = A.COURSE_ID AND C.USER_ID = A.USER_ID WHERE A.COURSE_ID = #{courseId}
AND UPPER(A.USER_ID) LIKE CONCAT('%',UPPER(#{searchStr}),'%')
AND UPPER(B.USER_NAME) LIKE CONCAT('%',UPPER(#{searchStr}),'%')
ORDER BY B.USER_NAME ) DATA LIMIT #{limitStart},#{limitUnit}

SELECT COUNT(*) CNT FROM COURSE_REGISTER A LEFT OUTER JOIN USER B ON A.USER_ID = B.USER_ID WHERE A.COURSE_ID = #{courseId}
AND UPPER(A.USER_ID) LIKE CONCAT('%',UPPER(#{searchStr}),'%')
AND UPPER(B.USER_NAME) LIKE CONCAT('%',UPPER(#{searchStr}),'%')

    SELECT EVAL,
           CONTENTS
	  FROM POSTSCRIPT
	 WHERE COURSE_ID = #{courseId}
	   AND USER_ID = #{userId}

mapper.update:
	UPDATE 	COURSE_EVAL
	SET		POSTSCRIPT_YN = 'Y'
	WHERE	COURSE_ID = #{courseId}
 	AND 	USER_ID = #{userId}
