mapper.update:
UPDATE QUEST_GROUP
   SET GROUP_NAME = #{GROUP_NAME},
       USE_YN = #{USE_YN},
       UPDATE_DATE = CURRENT_TIMESTAMP,
       UPDATE_USER = #{SESSION_USER_ID}
 WHERE QG_ID = #{QG_ID}

UPDATE QUEST
   SET TYPE = #{TYPE}
       ,QUESTION = #{QUESTION}
       ,QA1 = #{QA1}
       ,QA2 = #{QA2}
       ,QA3 = #{QA3}
       ,QA4 = #{QA4}
       ,USE_YN = #{USE_YN}
       ,ORD = #{ORD}
       ,UPDATE_DATE = CURRENT_TIMESTAMP
       ,UPDATE_USER = #{SESSION_USER_ID}
 WHERE QG_ID = #{QG_ID}   
   AND SEQ = #{SEQ}

mapper.delete:
DELETE FROM QUEST
 WHERE QG_ID = #{QG_ID}   
   AND SEQ = #{SEQ}

mapper.select:
	SELECT COUNT(*) CNT 
    FROM QUEST
   WHERE QG_ID = #{qgId}

UPDATE QUEST_GROUP
   SET GROUP_NAME = #{groupName},
       USE_YN = #{useYn},
       UPDATE_DATE = CURRENT_TIMESTAMP,
       UPDATE_USER = #{userId}
 WHERE QG_ID = #{qgId}

UPDATE COURSE_REGISTER
   SET QUEST_YN = 'Y'
 WHERE COURSE_ID = #{courseId}
   AND USER_ID = #{userId}

SELECT DATA.* FROM ( SELECT A.USER_ID ,B.USER_NAME ,B.EMAIL ,B.MOBILE ,IFNULL(E.ANSWER1_CNT,0) ANSWER1_CNT ,IFNULL(E.ANSWER2_CNT,0) ANSWER2_CNT ,IFNULL(E.ANSWER3_CNT,0) ANSWER3_CNT ,IFNULL(E.ANSWER4_CNT,0) ANSWER4_CNT ,A.QUEST_YN FROM COURSE_REGISTER A LEFT OUTER JOIN USER B ON A.USER_ID = B.USER_ID LEFT OUTER JOIN ( SELECT COURSE_ID, USER_ID, SUM(IF(ANSWER = '1',1,0)) ANSWER1_CNT, SUM(IF(ANSWER = '2',1,0)) ANSWER2_CNT, SUM(IF(ANSWER = '3',1,0)) ANSWER3_CNT, SUM(IF(ANSWER = '4',1,0)) ANSWER4_CNT FROM USER_QUEST GROUP BY COURSE_ID,USER_ID ) E ON E.COURSE_ID = A.COURSE_ID AND E.USER_ID = A.USER_ID WHERE A.COURSE_ID = #{courseId}
AND UPPER(A.USER_ID) LIKE CONCAT('%',UPPER(#{searchStr}),'%')
AND UPPER(B.USER_NAME) LIKE CONCAT('%',UPPER(#{searchStr}),'%')
ORDER BY B.USER_NAME ) DATA LIMIT #{limitStart},#{limitUnit}

SELECT COUNT(*) CNT FROM COURSE_REGISTER A LEFT OUTER JOIN USER B ON A.USER_ID = B.USER_ID LEFT OUTER JOIN ( SELECT COURSE_ID, USER_ID, SUM(IF(ANSWER = '1',1,0)) ANSWER1_CNT, SUM(IF(ANSWER = '2',1,0)) ANSWER2_CNT, SUM(IF(ANSWER = '3',1,0)) ANSWER3_CNT, SUM(IF(ANSWER = '4',1,0)) ANSWER4_CNT FROM USER_QUEST GROUP BY COURSE_ID,USER_ID ) E ON E.COURSE_ID = A.COURSE_ID AND E.USER_ID = A.USER_ID WHERE A.COURSE_ID = #{courseId}
AND UPPER(A.USER_ID) LIKE CONCAT('%',UPPER(#{searchStr}),'%')
AND UPPER(B.USER_NAME) LIKE CONCAT('%',UPPER(#{searchStr}),'%')

SELECT B.SEQ,
       B.QUESTION,
       B.TYPE,
       IF(B.TYPE = 'J',A.ANSWER,
         IF(A.ANSWER = '1',B.QA1,
           IF(A.ANSWER = '2',B.QA2,
             IF(A.ANSWER = '3',B.QA3,
               IF(A.ANSWER = '4',B.QA4,'')
             )
           )
         )
       ) ANSWER
  FROM USER_QUEST A LEFT OUTER JOIN QUEST B ON B.QG_ID = A.QG_ID AND B.SEQ = A.SEQ
 WHERE A.COURSE_ID = #{courseId}
   AND A.USER_ID = #{userId}
